const initState = [];

const DataReducer = (state = initState, action) => {
  console.log({ state, action });
  switch (action.type) {
    case "data/fetchData":
      console.log("data fetch:", action.data);
      return [action.data];
    case "data/deleteData":
      console.log("data 1:", action);
      let data = state.filter((item) => item.id !== action);
      return {
        data,
      };

    default:
      return state;
  }
};
export default DataReducer;
