import axios from "axios";

let api = "https://swapi.dev/api/starships";
let getData = [];

export const callAPIStarWar = async () => {
  do {
    let dataURL = await axios.get(api);
    api = dataURL.next;

    console.log("CallAPI STARWAR:");
    console.log(dataURL);
    getData = [...getData, ...dataURL.results];
  } while (api);

  return getData;
};
