          <div className="body-list" id="list1">
            <div className="item">
              <span>Max Speed</span>
              <span>665858</span>
            </div>
            <div className="item">
              {" "}
              <span>Credit</span>
              <span>5675686</span>
            </div>
            <div className="item">
              {" "}
              <span>Passengers</span>
              <span>me.passengers</span>
            </div>
            <div className="item">
              {" "}
              <span>Film Appearances</span>
              <span>56856865</span>
            </div>
          </div>
        </div>
        <div className="body-item">
          <div className="body-title">Storm Twin cloud car</div>
          <div className="body-image">
            <img src={starwars} alt="" />
          </div>
          <div className="body-list" id="list2">
            <div className="item">
              <span>Max Speed</span>
              <span>?</span>
            </div>
            <div className="item">
              {" "}
              <span>Credit</span>
              <span>?</span>
            </div>
            <div className="item">
              {" "}
