              <span>?</span>
            </div>
            <div className="item">
              {" "}
              <span>Film Appearances</span>
              <span>?</span>
            </div>
          </div>
        </div>
      </div>
      {/* <div className="footter">
      <button>Play Random</button>
    </div> */}
    </div>
  );
};

export default ExerciseContext;
