import axios from "axios";
import React, { useEffect, useState } from "react";
import starwars from "../../image/starwars.png";
import { connect } from "react-redux";
import {
  actFetchData,
  actDeleteData,
  actAddTotal,
  actDeleteTotal,
  actAddCount,
  actDeleteCount,
} from "../reducers/acction";
import { useDispatch, useSelector } from "react-redux";

const ExerciseRedux = (props) => {
  const dispatch = useDispatch();
  // const { data } = props;
  const data = useSelector((state) => state.datas[0]);
  const total = useSelector((state) => state.total.total);
  const count = useSelector((state) => state.counts.count);
  // const count2 = useSelector((state) => state.counts);
  console.log("cuong chi1: ", data);
  console.log("total chi1: ", total);
  console.log("count chi1: ", count);
  // console.log("count2 chi1: ", count2);

  const [loading, setLoading] = useState(true);
  const [show, setShow] = useState(false);
  // const [total, setTotal] = useState(0);

  // const [count, setCount] = useState(0);
  const [me, setMe] = useState({});

  const [count2, setCount2] = useState(0);
  const [machine, setMachine] = useState({});

  useEffect(() => {
    try {
      (async function () {
        let page = 1;
        let arr = [];
        let res = await axios.get(
          `https://swapi.dev/api/starships/?page=${page}`
        );
        let data = res && res.data ? res.data : [];
        arr = arr.concat(data.results);
        if (data.next) {
          page += 1;
          let res = await axios.get(
            `https://swapi.dev/api/starships/?page=${page}`
          );
          arr = arr.concat(res.data.results);
          if (data.next) {
            page += 1;
            let res = await axios.get(
              `https://swapi.dev/api/starships/?page=${page}`
            );
            arr = arr.concat(res.data.results);
            if (data.next) {
              page += 1;
              let res = await axios.get(
                `https://swapi.dev/api/starships/?page=${page}`
              );
              arr = arr.concat(res.data.results);
              dispatch(actFetchData(arr));
              setLoading(false);

              const index1 = Math.floor(Math.random() * arr.length);
              setMe(arr[index1]);
              console.log("Me cuong chi: ", index1, arr[index1]);
              props.deleteDataRedux(index1);
            } else {
              console.log("Hết data");
              return arr;
            }
          }
        }
      })();
    } catch (error) {
      console.log(error);
    }
  }, []);
  const handleClickMe = () => {
    // let data = datas[0];
    const index1 = Math.floor(Math.random() * data.length);
    setMe(data[index1]);
    console.log("Me: ", index1, data[index1]);
    dispatch(actAddTotal());

    // props.deleteDataRedux(index1);
    console.log("data index1: ", index1, data[index1]);
    dispatch(actDeleteData(index1));
  };
  const handleClickMachine = () => {
    // let data = datas[0];
    const index2 = Math.floor(Math.random() * data.length);
    setMachine(data[index2]);
    console.log("Machine: ", index2, data[index2]);
    // setTotal(total + 1);
    dispatch(actAddTotal());
    setShow(true);
    // props.deleteDataRedux(index2);
    dispatch(actDeleteData(index2));
  };
  const handleClickShow = () => {
    setShow(!show);
  };
  useEffect(() => {
    let a = parseInt(me.max_atmosphering_speed);
    let b = parseInt(machine.max_atmosphering_speed);
    if (a > b) {
      dispatch(actAddCount());
      document.getElementById("list1").style.background = "Green";
      document.getElementById("list2").style.background = "Red";
    } else if (a < b) {
      document.getElementById("list2").style.background = "Green";
      document.getElementById("list1").style.background = "Red";
      setCount2(count2 + 1);
    } else if (
      me.max_atmosphering_speed === "unknown" ||
      me.max_atmosphering_speed === "n/a" ||
      machine.max_atmosphering_speed === "unknown" ||
      machine.max_atmosphering_speed === "n/a"
    ) {
      document.getElementById("list2").style.background = "White";
      document.getElementById("list1").style.background = "White";
      // setCount(count);
      // setCount2(count2);
    }
    console.log(
      "1:",
      me.max_atmosphering_speed,
      "2: ",
      machine.max_atmosphering_speed
    );

    console.log("total: ", total);
    if (total === 11 && count > count2) {
      dispatch(actDeleteCount());
      setCount2(0);
      dispatch(actDeleteTotal());
      handleClickShow();
      document.getElementById("list1").style.background = "White";
      document.getElementById("list2").style.background = "White";
      return alert("You win!");
    } else if (total === 11 && count < count2) {
      dispatch(actDeleteCount());
      setCount2(0);
      dispatch(actDeleteTotal());
      handleClickShow();
      document.getElementById("list2").style.background = "White";
      document.getElementById("list1").style.background = "White";
      return alert("You lose!");
    } else if (total === 11 && count === count2) {
      dispatch(actDeleteCount());
      setCount2(0);
      dispatch(actDeleteTotal());
      handleClickShow();
      document.getElementById("list2").style.background = "White";
      document.getElementById("list1").style.background = "White";
      return alert("Draw!Draw!");
    }
  }, [me, machine]);
  const handleClickReset = () => {
    window.location.reload(false);
  };
  return (
    <div className="container2">
      {/* <h1> Xin chào Redux</h1> */}
      <div className="header-scores">
        <span>{count}</span>
        <span>{total}</span>
        <span>{count2}</span>
      </div>
      {loading === false ? (
        <div className="body">
          <div className="body-item">
            <div className="body-title" onClick={() => handleClickMe()}>
              SnowsPeeders
            </div>
            <div className="body-image">
              <img src={starwars} alt="" />
            </div>
            <div className="body-list" id="list1">
              <div className="item">
                <span>Max Speed</span>
                <span>{me.max_atmosphering_speed}</span>
              </div>
              <div className="item">
                {" "}
                <span>Credit</span>
                <span>{me.cost_in_credits}</span>
              </div>
              <div className="item">
                {" "}
                <span>Passengers</span>
                <span>{me.passengers}</span>
              </div>
              <div className="item">
                {" "}
                <span>Film Appearances</span>
                <span>{me.films?.length}</span>
              </div>
            </div>
          </div>
          <button
            className="reset"
            style={{ marginTop: "250px", fontSize: "20px" }}
            onClick={() => handleClickReset()}
          >
            Reset
          </button>
          <div className="body-item">
            <div className="body-title" onClick={() => handleClickMachine()}>
              Storm Twin cloud car
            </div>
            <div className="body-image">
              <img src={starwars} alt="" />
            </div>
            <div className="body-list" id="list2">
              <div className="item" onClick={() => handleClickShow()}>
                <span>Max Speed</span>
                <span>{show ? machine.max_atmosphering_speed : "?"}</span>
              </div>
              <div className="item">
                {" "}
                <span>Credit</span>
                <span>{show ? machine.cost_in_credits : "?"}</span>
              </div>
              <div className="item">
                {" "}
                <span>Passengers</span>
                <span>{show ? machine.passengers : "?"}</span>
              </div>
              <div className="item">
                {" "}
                <span>Film Appearances</span>
                <span>{show ? machine.films?.length : "?"}</span>
              </div>
            </div>
          </div>
        </div>
      ) : (
        <h2>Loading ...................</h2>
      )}
    </div>
  );
};
// const mapStateToProps = (state) => {
//   return {
//     data: state,
//   };
// };
// const mapDispatchToProps = (dispatch, props) => {
//   return {50
//     fetchAllData: (data) => {
//       dispatch(actFetchData(data));
//     },
//     deleteDataRedux: (id) => dispatch(actDeleteData(id)),
//     addTotalRedux: () => dispatch(actAddTotal()),
//     deleteTotalRedux: () => dispatch(actDeleteTotal()),
//   };
// };

export default ExerciseRedux;
