import * as Types from "./acctionTypes";
const initState = [];

const rootReducer = (state = initState, action) => {
  switch (action.type) {
    case Types.FETCH_DATA:
      state = action.data;
      return [...state];
    case Types.DELETE_DATA:
      var index = action.id;
      state.splice(index, 1);
      return [...state];

    default:
      return state;
  }
};
export default rootReducer;
