import axios from "axios";
import React, { useContext, useEffect, useState } from "react";
import starwars from "../../image/starwars.png";
import { connect } from "react-redux";
import { actFetchData, actDeleteData } from "../reducers/acction";

const ExerciseRedux = (props) => {
  const { data } = props;
  const [show, setShow] = useState(false);
  const [total, setTotal] = useState(0);

  const [count, setCount] = useState(0);
  const [me, setMe] = useState({});

  const [count2, setCount2] = useState(0);
  const [machine, setMachine] = useState({});

  useEffect(() => {
    try {
      (async function () {
        let page = 1;
        let arr = [];
        let res = await axios.get(
          `https://swapi.dev/api/starships/?page=${page}`
        );
        let data = res && res.data ? res.data : [];
        arr = arr.concat(data.results);
        if (data.next) {
          page += 1;
          let res = await axios.get(
            `https://swapi.dev/api/starships/?page=${page}`
          );
          arr = arr.concat(res.data.results);
          if (data.next) {
            page += 1;
            let res = await axios.get(
              `https://swapi.dev/api/starships/?page=${page}`
            );
            arr = arr.concat(res.data.results);
            if (data.next) {
              page += 1;
              let res = await axios.get(
                `https://swapi.dev/api/starships/?page=${page}`
              );
              arr = arr.concat(res.data.results);
              props.fetchAllData(arr);
            } else {
              console.log("Hết data");
              return arr;
            }
          }
        }
      })();
    } catch (error) {
      console.log(error);
    }
  }, []);
  const handleClickMe = () => {
    const index1 = Math.floor(Math.random() * data.length);
    setMe(data[index1]);
    console.log("Me: ", index1, data[index1]);
    setTotal(total + 1);
    // data.splice(index1, 1);
    props.deleteDataRedux(index1);
  };
  const handleClickMachine = () => {
    const index2 = Math.floor(Math.random() * data.length);
    setMachine(data[index2]);
    console.log("Machine: ", index2, data[index2]);
    data.splice(index2, 1);
    setTotal(total + 1);
    setShow(false);
  };
  console.log("data redux: ", data);
  return (
    <div className="container2">
      {/* <h1>Hello cac ban</h1> */}
      <div className="header-scores">
        <span>{count}</span>
        <span>{count2}</span>
      </div>
      <div className="body">
        <div className="body-item">
          <div className="body-title">SnowsPeeders</div>
          <div className="body-image">
            <img src={starwars} alt="" />
          </div>
          <div className="body-list" id="list1">
            <div className="item">
              <span>Max Speed</span>
              <span>{me.max_atmosphering_speed}</span>
            </div>
            <div className="item">
              {" "}
              <span>Credit</span>
              <span>{me.cost_in_credits}</span>
            </div>
            <div className="item">
              {" "}
              <span>Passengers</span>
              <span>{me.passengers}</span>
            </div>
            <div className="item">
              {" "}
              <span>Film Appearances</span>
              <span>{me.films?.length}</span>
            </div>
          </div>
        </div>
        <div className="body-item">
          <div className="body-title">Storm Twin cloud car</div>
          <div className="body-image">
            <img src={starwars} alt="" />
          </div>
          <div className="body-list" id="list2">
            <div className="item">
              <span>Max Speed</span>
              <span>{show ? machine.max_atmosphering_speed : "?"}</span>
            </div>
            <div className="item">
              {" "}
              <span>Credit</span>
              <span>{show ? machine.cost_in_credits : "?"}</span>
            </div>
            <div className="item">
              {" "}
              <span>Passengers</span>
              <span>{show ? machine.passengers : "?"}</span>
            </div>
            <div className="item">
              {" "}
              <span>Film Appearances</span>
              <span>{show ? machine.films?.length : "?"}</span>
            </div>
          </div>
        </div>
      </div>
      {/* <div className="footter">
      <button>Play Random</button>
    </div> */}
    </div>
  );
};
const mapStateToProps = (state) => {
  return {
    data: state,
  };
};
const mapDispatchToProps = (dispatch, props) => {
  return {
    fetchAllData: (data) => {
      dispatch(actFetchData(data));
    },
    deleteDataRedux: (item) => dispatch(actDeleteData(item)),
    // createUserRedux: (data) => dispatch({ type: "CREATE_USER", payload: data }),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(ExerciseRedux);
