import React from "react";
import starwars from "../../image/starwars.png";

const CardMachine = (props) => {
  return (
    <div className="body-item">
      <div className="body-title" onClick={props.handleClickMachine}>
        {" "}
        Storm Twin cloud car
      </div>
      <div className="body-image">
        <img src={starwars} alt="" />
      </div>
      <div className="body-list" id="list2">
        <div className="item" onClick={props.handleClickShow}>
          <span>Max Speed</span>
          <span>{props.show ? props.machine.max_atmosphering_speed : "?"}</span>
        </div>
        <div className="item">
          {" "}
          <span>Credit</span>
          <span>{props.show ? props.machine.cost_in_credits : "?"}</span>
        </div>
        <div className="item">
          {" "}
          <span>Passengers</span>
          <span>{props.show ? props.machine.passengers : "?"}</span>
        </div>
        <div className="item">
          {" "}
          <span>Film Appearances</span>
          <span>{props.show ? props.machine.films?.length : "?"}</span>
        </div>
      </div>
    </div>
  );
};

export default CardMachine;
