const initState = {
  total: 0,
};

const TotalReducer = (state = initState, action) => {
  console.log({ state, action });
  switch (action.type) {
    case "total/addTotal":
      return {
        ...state,
        search: action.payload,
      };
    case "total/resetTotal":
      return {
        ...state,
        search: action.payload,
      };
    default:
      return state;
  }
};
export default TotalReducer;
