const initState = [];

const DataReducer = (state = initState, action) => {
  console.log({ state, action });
  switch (action.type) {
    case "data/fetchData":
      return {
        ...state,
        search: action.payload,
      };
    case "data/deleteData":
      return {
        ...state,
        status: action.payload,
      };

    default:
      return state;
  }
};
export default DataReducer;
