import logo from "./logo.svg";
import "./App.css";
import Component from "./views/Component/Component";
import Exercise2 from "./views/Exercise2/Exercise2";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import ExerciseContext from "./views/Exercise-context/Exercise-react-context";
import ExerciseRedux from "./views/Exercise-redux/Exercise-redux";
import { useEffect, useState } from "react";
import axios from "axios";
import { createContext } from "react";

export const ThemeContext = createContext();
export const TotalContext = createContext();
function App() {
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState([]);
  const [total, setTotal] = useState(0);
  useEffect(() => {
    try {
      (async function () {
        let arr = [];
        let page = 1;
        let res = await axios.get(
          `https://swapi.dev/api/starships/?page=${page}`
        );
        let data = res && res.data ? res.data : [];
        arr = arr.concat(data.results);
        if (data.next) {
          page += 1;
          let res = await axios.get(
            `https://swapi.dev/api/starships/?page=${page}`
          );
          arr = arr.concat(res.data.results);
          if (data.next) {
            page += 1;
            let res = await axios.get(
              `https://swapi.dev/api/starships/?page=${page}`
            );
            arr = arr.concat(res.data.results);
            if (res.data.next) {
              page += 1;
              let res = await axios.get(
                `https://swapi.dev/api/starships/?page=${page}`
              );
              arr = arr.concat(res.data.results);
              console.log("array 4:", page, arr);
            } else {
              console.log("đã hết api");
              return arr;
            }
          }
        }
        setData(arr);
        setLoading(false);
      })();
    } catch (error) {
      console.log(error);
    }
  }, []);
  return (
    <div className="App">
      <ThemeContext.Provider value={data}>
        <TotalContext.Provider value={total}>
          <BrowserRouter>
            <Routes>
              <Route path="/" element={<Component />} />
              <Route path="/exercise" element={<Exercise2 />} />
              <Route
                path="/exercise-react-context"
                element={
                  <ExerciseContext loading={loading} setTotal={setTotal} />
                }
              />
              <Route path="/exercise-redux" element={<ExerciseRedux />} />
            </Routes>
          </BrowserRouter>
        </TotalContext.Provider>
      </ThemeContext.Provider>
    </div>
  );
}
export default App;
