  const [count2, setCount2] = useState(0);
  const [machine, setMachine] = useState({});
  const [showMachine, setShowMachine] = useState(false);

  useEffect(() => {
    try {
      (async function () {
        let arr = [];
        let page = 1;
        let res = await axios.get(
          `https://swapi.dev/api/starships/?page=${page}`
        );
        let data = res && res.data ? res.data : [];
        arr = arr.concat(data.results);
        // console.log("array 1:", arr);
        if (data.next) {
          page += 1;
          let res = await axios.get(
            `https://swapi.dev/api/starships/?page=${page}`
          );
          arr = arr.concat(res.data.results);
          // console.log("array 2:", page, arr);
          if (res.data.next) {
            page += 1;
            let res = await axios.get(
              `https://swapi.dev/api/starships/?page=${page}`
            );
            arr = arr.concat(res.data.results);
            // console.log("array 3:", page, arr);
            setData(arr);
            if (res.data.next) {
              page += 1;
              let res = await axios.get(
                `https://swapi.dev/api/starships/?page=${page}`
              );
              arr = arr.concat(res.data.results);
              console.log("array 4:", page, arr);
              setData(arr);
              setLoading(false);
              const index1 = Math.floor(Math.random() * arr.length);
              setMe(arr[index1]);
              console.log("Me cuong chi: ", index1, arr[index1]);
              arr.splice(index1, 1);
              if (res.data.next) {
                page += 1;
                let res = await axios.get(
                  `https://swapi.dev/api/starships/?page=${page}`
                );
                arr = arr.concat(res.data.results);
                console.log("array 4:", page, arr);
                setData(arr);
                const index1 = Math.floor(Math.random() * arr.length);
                setMe(arr[index1]);
                console.log("Me cuong chi: ", index1, arr[index1]);
                arr.splice(index1, 1);
              } else {
                console.log("đã hết dữ liệu 2");
                return arr;
              }
            } else {
              console.log("đã hết dữ liệu");
              return arr;
            }
          }
