import {
  FETCH_POSTS_REQUEST,
  FETCH_POSTS_SUCCESS,
  FETCH_POSTS_ERROR,
  RANDOM_ME,
} from "./acctionTypes";

const initState = {
  requesting: false,
  success: false,
  message: null,
  data: [],
  randomMe: {},
  randomMachine: {},
};

const DataReducer = (state = initState, action) => {
  console.log({ state, action });
  switch (action.type) {
    case FETCH_POSTS_REQUEST:
      return { ...state, requesting: true };
    case FETCH_POSTS_SUCCESS:
      console.log("data fetch:", action.data);
      return {
        ...state,
        requesting: false,
        success: true,
        data: action.data,
      };
    case FETCH_POSTS_ERROR:
      return {
        ...state,
        requesting: false,
        success: false,
        message: action.message,
      };
    case RANDOM_ME:
      const index1 = Math.floor(Math.random() * state.data.length);
      console.log("index *********:", index1);
      let dataNew = state.data.filter((item) => item.id !== index1);
      return {
        ...state,
        requesting: false,
        // success: true,
        data: dataNew,
        randomMe: state.data[index1],
      };
    // case DELETE_ITEM:
    //   console.log("data 1:", action);
    //   // let dataNew = state.data.filter((item) => item.id !== action);
    //   return { ...state, requesting: false, success: true, data: dataNew };

    default:
      return state;
  }
};
export default DataReducer;
