import React, { useEffect } from "react";
import starwars from "../../image/starwars.png";

const CardMe = (props) => {
  useEffect(() => {
    props.handleClickMe();
  }, []);

  return (
    <div className="body-item">
      <div className="body-title" onClick={props.handleClickMe}>
        SnowsPeeders
      </div>
      <div className="body-image">
        <img src={starwars} alt="" />
      </div>
      <div className="body-list" id="list1">
        <div className="item">
          <span>Max Speed</span>
          <span>{props.me.max_atmosphering_speed}</span>
        </div>
        <div className="item">
          {" "}
          <span>Credit</span>
          <span>{props.me.cost_in_credits}</span>
        </div>
        <div className="item">
          {" "}
          <span>Passengers</span>
          <span>{props.me.passengers}</span>
        </div>
        <div className="item">
          {" "}
          <span>Film Appearances</span>
          <span>{props.me.films?.length}</span>
        </div>
      </div>
    </div>
  );
};

export default CardMe;
