import React from "react";
import { useEffect, useState } from "react";
import axios from "axios";
import "./Component.scss";

const Component = () => {
  const [data, setData] = useState([]);
  const [dataShow, setDataShow] = useState({});
  const [listData, setListData] = useState([]);
  const [total, setTotal] = useState(6);
  const [count, setCount] = useState(1);
  useEffect(() => {
    try {
      (async function () {
        let res = await axios.get(
          "https://run.mocky.io/v3/d03e0886-f5c8-4961-902d-51bfe8059a33"
        );
        let data = res.data ? res.data : [];
        setData(data);
        setDataShow(data[0]);
        setListData(data.slice(0, total));

        console.log("data:", data);
        console.log("data Show: ", dataShow);
        console.log("list data: ", listData);
      })();
    } catch (error) {
      console.log(error);
    }
  }, []);
  const handleClick = (item) => {
    setDataShow(item);
  };
  const handlePrev = () => {
    setCount(count - 1);
    // setListData(data.slice(total,total));
    console.log("count: ", count);

    let end = total * count;
    setListData(data.slice(end - total, end));
  };
  const handleNext = () => {
    setCount(count + 1);
    // setListData(data.slice(total,total));
    console.log("count: ", count);
    let end = total * count;
    setListData(data.slice(end - total, end));
  };
  return (
    <div className="container">
      <div className="body">
        <div className="body-left">
          <img src={dataShow.Poster} alt="" />
        </div>
        <div className="body-right">
          <div className="title">{dataShow.Title}</div>
          <div className="information">
            <ul>
              <li>{dataShow.Actors}</li>
              <li>{dataShow.Director}</li>
              <li>{dataShow.Plot}</li>
              {/* <li>{dataShow.Writer}</li> */}
              <li>{dataShow.Title}</li>
              <li>{dataShow.Year}</li>
            </ul>
          </div>
        </div>
      </div>
      <div className="footer">
        <div className="footer-select">
          <select>
            <option value="one">One</option>
            <option value="tow">Tow</option>
            <option value="three">Three</option>
          </select>
        </div>
        <div className="prev" onClick={() => handlePrev()}>
          Prev
        </div>
        <div className="footer-list">
          {listData.map((item, index) => {
            return (
              <div className="list" key={index}>
                <div className="item">
                  <img
                    src={item.Poster}
                    alt=""
                    width={{ width: "100%" }}
                    onClick={() => handleClick(item)}
                  />
                </div>
                {/* <div className="item">list2</div>
              <div className="item">list3</div>
              <div className="item">list4</div>
              <div className="item">list5</div>
              <div className="item">list6</div>
              <div className="item">list6</div>
              <div className="item">list6</div>

              <div className="item">list6</div> */}
              </div>
            );
          })}
          {/* <div className="list">
            <div className="item">list1</div>
            <div className="item">list2</div>
            <div className="item">list3</div>
            <div className="item">list4</div>
            <div className="item">list5</div>
            <div className="item">list6</div>
            <div className="item">list6</div>
            <div className="item">list6</div>

            <div className="item">list6</div>
          </div> */}
        </div>
        <div className="next" onClick={() => handleNext()}>
          Next
        </div>
      </div>
    </div>
  );
};

export default Component;
