import {
  FETCH_POSTS_REQUEST,
  FETCH_POSTS_SUCCESS,
  FETCH_POSTS_ERROR,
} from "./acctionTypes";

const initState = {
  requesting: false,
  success: false,
  message: null,
  data: null,
};

const DataReducer = (state = initState, action) => {
  console.log({ state, action });
  switch (action.type) {
    case FETCH_POSTS_REQUEST:
      return { ...state, requesting: true };
    case FETCH_POSTS_SUCCESS:
      console.log("data fetch:", action.data);
      return {
        ...state,
        requesting: false,
        success: true,
        data: action.data,
      };
    case FETCH_POSTS_ERROR:
      return {
        ...state,
        requesting: false,
        success: false,
        message: action.message,
      };
    case "data/fetchData":
      console.log("data fetch:", action.data);
      return [action.data];
    case "data/deleteData":
      console.log("data 1:", action);
      let data = state.data.filter((item) => item.id !== action);
      return [...data];

    default:
      return state;
  }
};
export default DataReducer;
