import {
  FETCH_POSTS_REQUEST,
  FETCH_POSTS_SUCCESS,
  FETCH_POSTS_ERROR,
  RANDOM_ME,
  RANDOM_MACHINE,
} from "../acctionTypes";

const initState = {
  requesting: false,
  success: false,
  message: null,
  data: [],
  randomMe: {},
  randomMachine: {},
};

const DataReducer = (state = initState, action) => {
  console.log({ state, action });
  switch (action.type) {
    case FETCH_POSTS_REQUEST:
      return { ...state, requesting: true };
    case FETCH_POSTS_SUCCESS:
      return {
        ...state,
        requesting: false,
        success: true,
        data: action.data,
        randomMe: action.random,
        randomMachine: {},
      };
    case FETCH_POSTS_ERROR:
      return {
        ...state,
        requesting: false,
        success: false,
        message: action.message,
      };
    case RANDOM_ME:
      let index1 = Math.floor(Math.random() * state.data.length - 1);
      console.log("index *********:", index1);
      let dataNew = state.data.splice(index1, 1);
      console.log("dataNew: *****:", dataNew);
      // let indexNew = Math.floor(Math.random() * state.data.length);
      // let randomNew = state.data[index1]
      //   ? state.data[index1]
      //   : state.data[indexNew];
      return {
        ...state,
        requesting: false,
        success: true,
        // data: dataNew,
        randomMe: state.data[index1],
      };
    case RANDOM_MACHINE:
      let index2 = Math.floor(Math.random() * state.data.length - 1);
      console.log("index *********:", index2);
      state.data.splice(index2, 1);
      // let indexNew2 = Math.floor(Math.random() * state.data.length);
      // let randomNew2 = state.data[index2]
      //   ? state.data[index2]
      //   : state.data[indexNew2];
      return {
        ...state,
        requesting: false,
        success: true,
        // data: dataNew,
        randomMachine: state.data[index2],
      };
    // case DELETE_ITEM:
    //   console.log("data 1:", action);
    //   // let dataNew = state.data.filter((item) => item.id !== action);
    //   return { ...state, requesting: false, success: true, data: dataNew };

    default:
      return state;
  }
};
export default DataReducer;
