import React, { useContext, useEffect, useState } from "react";
import { ThemeContext } from "../../App";
import CardMachine from "./CardMachine";
import CardItem from "./CardMachine";
import CardMe from "./CardMe";
import Count from "./Count";

const ExerciseContext = (props) => {
  const data = useContext(ThemeContext);
  const total = useContext(ThemeContext);
  const setTotal = useContext(ThemeContext);
  const count = useContext(ThemeContext);
  const setCount = useContext(ThemeContext);
  const count2 = useContext(ThemeContext);
  const setCount2 = useContext(ThemeContext);

  const [show, setShow] = useState(false);
  // const [total, setTotal] = useState(0);
  const [me, setMe] = useState({});
  // const [count, setCount] = useState(0);

  const [machine, setMachine] = useState({});
  // const [count2, setCount2] = useState(0);

  // console.log("data 10:", data);
  const handleClickMe = () => {
    const index1 = Math.floor(Math.random() * data.length);
    setMe(data[index1]);
    // console.log("Me: ", index1, data[index1]);
    data.splice(index1, 1);
    setTotal(total + 1);
  };

  const handleClickMachine = () => {
    const index2 = Math.floor(Math.random() * data.length);
    setMachine(data[index2]);
    // console.log("Machine: ", index2, data[index2]);
    data.splice(index2, 1);
    setTotal(total + 1);
  };
  useEffect(() => {
    let a = parseInt(me.max_atmosphering_speed);
    let b = parseInt(machine.max_atmosphering_speed);
    if (a > b) {
      setCount(count + 1);
      document.getElementById("list1").style.background = "Green";
      document.getElementById("list2").style.background = "Red";
    } else if (a < b) {
      document.getElementById("list2").style.background = "Green";
      document.getElementById("list1").style.background = "Red";
      setCount2(count2 + 1);
    } else if (
      me.max_atmosphering_speed === "unknown" ||
      me.max_atmosphering_speed === "n/a" ||
      machine.max_atmosphering_speed === "unknown" ||
      machine.max_atmosphering_speed === "n/a"
    ) {
      document.getElementById("list2").style.background = "White";
      document.getElementById("list1").style.background = "White";
      setCount(count);
      setCount2(count2);
    }
    console.log(
      "1:",
      me.max_atmosphering_speed,
      "2: ",
      machine.max_atmosphering_speed
    );
    console.log("total: ", total);
    if (total === 10 && count > count2) {
      setCount(0);
      setCount2(0);
      setTotal(0);
      document.getElementById("list1").style.background = "White";
      document.getElementById("list2").style.background = "White";
      return alert("You win! ");
    } else if (total === 10 && count < count2) {
      setCount(0);
      setCount2(0);
      setTotal(0);
      document.getElementById("list2").style.background = "White";
      document.getElementById("list1").style.background = "White";
      return alert("You lose!");
    } else if (total === 10 && count === count2) {
      setCount(0);
      setCount2(0);
      setTotal(0);
      document.getElementById("list2").style.background = "White";
      document.getElementById("list1").style.background = "White";
      return alert("Draw! ");
    }
  }, [me, machine]);
  const handleClickShow = () => {
    setShow(!show);
  };

  // console.log("loading: ", props.loading);
  return (
    <div className="container2">
      {/* <div className="header-scores">
        <span>{count}</span>
        <span>{count2}</span>
      </div> */}
      <Count count={count} count2={count2} />
      {props.loading === false ? (
        <div className="body">
          <CardMe handleClickMe={handleClickMe} me={me} />
          <CardMachine
            handleClickMachine={handleClickMachine}
            handleClickShow={handleClickShow}
            machine={machine}
            show={show}
          />
        </div>
      ) : (
        <h2>Loading.............</h2>
      )}
    </div>
  );
};

export default ExerciseContext;
