  //         let dataURL = await axios.get(api);
  //         api = dataURL.data.next;

  //         const results = await dataURL.data.results;
  //         getData = [...getData, ...results];
  //         setData(getData);
  //         setLoading(false);
  //       } while (api);
  //       let index1 = Math.floor(Math.random() * (getData.length - 1));
  //       setMe(getData[index1]);
  //       getData.splice(index1, 1);
  //     })();
  //   } catch (error) {
  //     console.log(error);
  //   }
  // }, [total === 11]);
  useEffect(() => {
    try {
      (async function () {
        let arr = [];
        let page = 1;
        let res = await axios.get(
          `https://swapi.dev/api/starships/?page=${page}`
        );
        let data = res && res.data ? res.data : [];
        arr = arr.concat(data.results);
        if (data.next) {
          page += 1;
          let res = await axios.get(
            `https://swapi.dev/api/starships/?page=${page}`
          );
          arr = arr.concat(res.data.results);
          if (data.next) {
            page += 1;
            let res = await axios.get(
              `https://swapi.dev/api/starships/?page=${page}`
            );
            arr = arr.concat(res.data.results);
            if (res.data.next) {
              page += 1;
              let res = await axios.get(
                `https://swapi.dev/api/starships/?page=${page}`
              );
              arr = arr.concat(res.data.results);
              console.log("array 4:", page, arr);
              setData(arr);
            } else {
              console.log("đã hết api");
              return arr;
            }
          }
        }
        setData(arr);
        setLoading(false);
      })();
    } catch (error) {
      console.log(error);
    }
  }, [total === 10]);
  // useEffect(async () => {
  //   const index1 = await Math.floor(Math.random() * data.length);
  //   setMe(data[index1]);
  //   console.log("Me cuong chi: ", index1, data[index1]);
  //   data.splice(index1, 1);
  // }, []);

  // const handleClickMe = () => {
  //   const index1 = Math.floor(Math.random() * data.length);
  //   setMe(data[index1]);
  //   console.log("Me: ", index1, data[index1]);
