const initState = [{ count: 0 }, { count2: 1 }];

const CountReducer = (state = initState, action) => {
  console.log({ state, action });
  switch (action.type) {
    case "countAdd":
      console.log("data fetch:", action.data);
      return [action.data];
    case "countDelete":
      console.log("data 1:", action);
      let data = state.filter((item) => item.id !== action);
      return [...data];

    default:
      return state;
  }
};
export default CountReducer;
