import * as Types from "./acctionTypes";
export const actAddTotal = (data) => {
  return {
    type: "total/addTotal",
    data,
  };
};
export const actDeleteTotal = () => {
  return {
    type: "total/deleteTotal",
  };
};

export const actFetchData = (data) => {
  return {
    type: "data/fetchData",
    data,
  };
};
export const actDeleteData = (id) => {
  return {
    type: "data/deleteData",
    id,
  };
};

